/*
Three main components (consisting of card components):
Face-down Draw Pile (Deck of 52 or less)
Face-up Discard Pile (Deck of 0 or less)
Hand (array of 4 distinct cards)

*/

import React from "react";
import { render } from "react-dom";
import Deck from "../node_modules/deck.js"

class Hand extends React.Component {
  /*
  Four face down cards
  */
  render(){
    return (
      [
      <button>Card 1</button>,
      <button>Card 2</button>,
      <button>Card 3</button>,
      <button>Card 4</button>
      ]
    )
  };
};

class DrawPile extends React.Component {
  /*
  Display next card face down
  1. On click, reveal card
  2a. Click on discard pile to discard
  2b. Click on a card in hand to swap
  */
  render(){
    return (
      <button>Draw Pile</button>
    )
  };
};

class DiscardPile extends React.Component {
  //Display last discarded card
  render(){
    return (
        <button>Discard Pile</button>
    );
  };
};

class Game extends React.Component {
  /*
  State for turn, different actions for start and end
  */
  constructor(props){
    super(props);
    this.state = {
      drawpile: Deck([1,2,3])
    };
  }
  onDrawPileClick(){};
  onDiscardPileClick(){};
  onHandClick(){};
  render(){
    return (
      <div>
        <header>Cambio!!</header>
        <header1>Draw</header1>
        <DrawPile/>
        <header1>Discard</header1>
        <DiscardPile/>
        <header1>Your Hand</header1>
        <Hand/>
      </div>
    )
  };
}

export default Game;
