import React from "react";

function LastDiscard (props) {
    if(props.stick){
        return (
            <button onClick={props.handleStick}>Last Discarded: {props.last_discarded}</button>
        )
    }
    return(<div>Last Discarded: {props.last_discarded}</div>)
}

class DiscardPile extends React.Component {
    //Display last discarded card
    render(){
        if(!this.props.stick){
        return (
          <div>
            <button onClick = {this.props.handleDiscard}>Discard</button>
            {LastDiscard(this.props)}
          </div>
        );
      }
    };
  };

export default DiscardPile;
