/*
Three main components (consisting of card components):
Face-down Draw Pile (Deck of 52 or less)
Face-up Discard Pile (Deck of 0 or less)
Hand (array of 4 distinct cards)

*/

import Deck, { shuffle } from "card-deck";
import React from "react";
import { render } from "react-dom";
//import { Deck } from "card-deck"
//import Deck from "react";

class Cambio extends React.Component {
  render(){
    return (
      <button onClick = {this.props.onClick}>Cambio!!</button>
    )
  };
};

class Card extends React.Component {
  render(){
    //if(this.props.value === null){return;}
    return (<button onClick = {this.props.onClick}>Card {this.props.pos}: {this.props.value}</button>) 
  };
};

class Hand extends React.Component {
 renderCard(i){
   return <Card onClick = {() => this.props.onClick(i)} pos = {i + 1} value = {this.props.hand[i]}/>
 }
render(){
  var cardHand = [];
  for(var i = 0; i < this.props.hand.length; i++){
    cardHand.push(this.renderCard(i));
  }
    return (
      <div>{cardHand}</div>
      /*
      <div>
        <div className="card-row">
          {this.renderCard(2)}
          {this.renderCard(3)}
        </div>
        <div className="card-row">
        {this.renderCard(0)}
          {this.renderCard(1)}
        </div>
      </div>
      */
    )
  };
};

class DrawPile extends React.Component {
  /*
  Display next card face down
  1. On click, reveal card
  2a. Click on discard pile to discard
  2b. Click on a card in hand to swap
  */
  render(){
    if(this.props.swap){
      return (
        <div>
          <button onClick = {this.props.onClick}>Draw</button>
          <div>Currently Drawn: <button>Swap: {this.props.card}</button></div>
        </div>
      )
    }
    return (
      <div>
        <button onClick = {this.props.onClick}>Draw</button>
        <div>Currently Drawn: {this.props.card}</div>
      </div>
    )
  };
};

class DiscardPile extends React.Component {
  //Display last discarded card
  render(){
    return (
      <div>
        <button onClick = {this.props.onClick}>Discard</button>
        <div>Last Discarded: <button>{this.props.card}</button>
        </div>
      </div>
    );
  };
};

class Game extends React.Component {
  /*
  State for turn, different actions for start and end
  */
  constructor(props){
    super(props);
    this.state = {
      start: true,
      // Decks
      drawpile: new Deck([]),
      discardpile: new Deck([]),
      // actions allowed on turn
      stick: false,
      draw: false,
      swap: false,
      discard: false,

      last_drawn: null, // top of draw pile
      last_discarded: null, // top of discard pile

      // Hand
      hand: [null, null, null, null],
    };
  };
  onStart(){
    if(!this.state.start){return;}
    var cards = [
      ["2", "D"],["2", "C"],["2", "H"],["2", "S"],
      ["3", "D"],["3", "C"],["3", "H"],["3", "S"],
      ["4", "D"],["4", "C"],["4", "H"],["4", "S"],
      ["5", "D"],["5", "C"],["5", "H"],["5", "S"],
      ["6", "D"],["6", "C"],["6", "H"],["6", "S"],
      ["7", "D"],["7", "C"],["7", "H"],["7", "S"],
      ["8", "D"],["7", "C"],["7", "H"],["7", "S"],
      ["9", "D"],["9", "C"],["9", "H"],["9", "S"],
      ["10", "D"],["10", "C"],["10", "H"],["10", "S"],
      ["j", "D"],["j", "C"],["j", "H"],["j", "S"],
      ["q", "D"],["q", "C"],["q", "H"],["q", "S"],
      ["k", "D"],["k", "C"],["k", "H"],["k", "S"],
      ["a", "D"],["a", "C"],["a", "H"],["a", "S"],
    ];
    var playing_cards = new Deck(cards);
    playing_cards.shuffle();
    this.setState({
      start: false,
      draw: true,
      hand: playing_cards.draw(4),
      drawpile: playing_cards,
    })
  }
  onDrawPileClick(){
    if(!this.state.draw || this.state.last_discarded === null){return;}
    var card = this.state.drawpile.draw(1);
    if(!card){return;} // no cards to draw
    this.setState({
      draw: false,
      swap: false,
      stick: false,
      discard: true,
      last_drawn: card,
      drawpile: this.state.drawpile,
    })

  };
  onDiscardPileClick(){
    if(!this.state.discard){return;}
    this.setState({
      draw: true,
      swap: false,
      discard: false,
      discardpile: this.state.discardpile.addToTop(this.state.last_drawn),
      last_discarded: this.state.last_drawn,
      last_drawn: null,
    })
  };
  onHandClick(i){
    if(this.state.start || !this.state.discard){return;}
    //var card_swapped = this.state.hand[i];
    //var new_hand = this.state.hand;
    //new_hand[i] = this.state.last_drawn;
    if(this.state.swap && this.state.stick){
      this.setState({
        swap: false,
        stick: false,
      })
    }
    else{
      this.setState({
        swap: true,
        stick: true,
        //hand: new_hand,
        //last_drawn: card_swapped,
      })
    }
  };


  handScore(){
    var hand = [
    this.state.hand[0],
    this.state.hand[1],
    this.state.hand[2],
    this.state.hand[3],
    ];
    var total_score = 0;
    for(var i = 0; i < 4; i++){
      var card = hand[i];
      if(card[0] === "k" && (card[1] === "D" || card[1] === "H")){
        total_score -= 1;
      }
      else if(card[0] === "j" || card[0] === "q" || card[0] === "k"){
        total_score += 10;
      }
      else if(card[0] === "a"){
        total_score += 1;
      }
      else{
        total_score += parseInt(card[0]);
      }
    }
    return total_score;
  };
  callCambio(){
    if(this.state.start){return;}
    var total_score = this.handScore();
    alert("Score: " + total_score);
    this.setState({
      start: true,
      draw: false,
      swap: false,
      discard: false,

      drawpile: new Deck([]),
      discardpile: new Deck([]),
      last_discarded: null,
      last_drawn: null,
      hand: [null, null, null, null],
    })
  };


  render(){
    //diamonds(D), cloves(C), hearts(H), spades(S)
    //2-10, j,q,k,a
    return (
      <div>
        <Cambio 
        onClick = {() => this.callCambio()}
        />
        <button onClick = {() => this.onStart()}>start</button>
        <div>
          <DrawPile
          onClick = {() => this.onDrawPileClick()}
          card = {this.state.last_drawn}
          swap = {this.state.swap}
          />
          <DiscardPile
          onClick = {() => this.onDiscardPileClick()}
          card = {this.state.last_discarded}
          stick = {this.state.stick}
          />
        </div>
        <div>
          <header1>Your Hand: </header1>
          <Hand
          onClick = {(i) => this.onHandClick(i)}
          hand = {this.state.hand}
          />
        </div>

      </div>
    )
  };
};

export default Game;
