import React from "react";

class LastDiscard extends React.Component {
    render(){
        if(this.props.stick){
            return (
                <button onClick={this.props.handleStick}>Last Discarded: {this.props.last_discarded}</button>
            )
        }
        return(<div>{this.props.last_discarded}</div>)
    }
}

class DiscardPile extends React.Component {
    //Display last discarded card
    render(){
        if(!this.props.stick){
        return (
          <div>
            <button onClick = {this.props.handleDiscard}>Discard</button>
            <LastDiscard/>
          </div>
        );
      }
    };
  };

export default DiscardPile;
